import com.google.protobuf.util.JsonFormat
import agl.VssNotification
def protobufClass = agl.VssNotification.SignalUpdateNotification

flowFile = session.get()
if (!flowFile) return

try {
    flowFile = session.write(flowFile, { inputStream, outputStream ->
        def bytes = inputStream.bytes
        def message = protobufClass.parseFrom(bytes)
        def json = JsonFormat.printer().includingDefaultValueFields().print(message)
        outputStream.write(json.bytes)
    } as StreamCallback)
    session.transfer(flowFile, REL_SUCCESS)
} catch (Exception e) {
    log.error("Error al convertir Protobuf a JSON: " + e.message, e)
    session.transfer(flowFile, REL_FAILURE)
}

