import org.apache.commons.io.IOUtils
import java.nio.charset.*
import groovy.json.JsonSlurper;
import java.time.LocalDate;
import java.time.ZoneId;
import java.util.Calendar;
import java.util.Date;
import groovy.json.JsonOutput 

def flowFile = session.get();
if(!flowFile) return

if (flowFile!= null){

  def jsonSlurper = new JsonSlurper();
  def object;
  def amqp_rotingkey = flowFile.getAttribute('amqp$routingKey');
  def format = "yyyy-MM-dd'T'HH:mm:ss.SSSSSS'Z'";
  def format_timezone = "yyyy-MM-dd'T'HH:mm:ss.SSSSSSXXX";
  def duration_multiplier = 1000;
  def amqp_exchange = flowFile.getAttribute('amqp$exchange').toLowerCase();
  def msg_identifier='';
  def msg_seq='';
  def ref_time_fix;
  def ref_time;
  def validation_to;
  def validation_duration=''; 
  def msg_timeStamp; 
  def content='';
  def reference_latitude, reference_longitude,customer_id,actionObject='';
  def msg_iden_seq;
  def amq_msg = amqp_rotingkey.tokenize(".")[3]

  flowFile = session.write(flowFile, {inputStream, outputStream ->
      //def text = IOUtils.toString(inputStream, StandardCharsets.UTF_8).replaceAll('\\\\', '');
      def text = IOUtils.toString(inputStream, StandardCharsets.UTF_8).replaceAll('\\\\', '').replaceAll('\n','').replace('<?xml version=\"1.0\"?>','');
      object = jsonSlurper.parseText(text);
      if (object.denm != null || object.ivim != null ){
        if (object.denm != null){
          content = object.denm;
          msg_identifier = object.actionIdStationId.toString();
          msg_seq = object.actionIdSequenceNumber.toString();
          msg_timeStamp = object.referenceTime;
          reference_latitude = object.referencePositionLat.toString();
          reference_longitude = object.referencePositionLon.toString();
          customer_id = object.customerId;
        }else if (object.ivim != null){
            content = object.ivim;
            actionObject = object.ivimType
            msg_identifier = object.ivimId.toString();
            msg_timeStamp = object.timestamp;
            reference_latitude = object.referencePositionLat.toString();
            reference_longitude = object.referencePositionLon.toString();
            customer_id = object.customerId;
        }
        if (object.referenceTimez!=null){
          ref_time_fix= object.referenceTimez;
        }else{
            ref_time_fix ='';
        }
        if (object.validTo!=null){
          validation_to = object.validTo;
        }
        if (object.validityDuration!=null){
          validation_duration = object.validityDuration.toString();
        }
        if(object.referenceTime!=null){
          ref_time = object.referenceTime;
        }else {
          ref_time ='';
        }
      }
      outputStream.write(content.getBytes(StandardCharsets.UTF_8));

  } as StreamCallback)
if (object.denm != null || object.ivim != null ){
  // putting attributes:   
  session.putAttribute(flowFile, "relay.errors","true");
  session.putAttribute(flowFile, "provider","GENERATOR");
  session.putAttribute(flowFile, "message.contentIndex",amq_msg);

  session.putAttribute(flowFile, "message.identifier",msg_identifier);
  session.putAttribute(flowFile, "message.identifier.sequence",msg_seq);
  session.putAttribute(flowFile, "message.timestamp",msg_timeStamp);
  session.putAttribute(flowFile, "reference.latitude",reference_latitude);
  session.putAttribute(flowFile, "reference.longitude",reference_longitude);
  session.putAttribute(flowFile, "customer.id",customer_id);
  session.putAttribute(flowFile, "action",actionObject);
  session.putAttribute(flowFile, "reference.time.fix",ref_time_fix);
  session.putAttribute(flowFile, "reference.time",ref_time);
  session.putAttribute(flowFile, "validation.duration",validation_duration);
  session.putAttribute(flowFile, "validation.to",validation_to.toString());

  // second processor
  if (msg_seq!=null && !msg_seq.empty){
    msg_iden_seq =  msg_identifier + "-" + msg_seq;
    session.putAttribute(flowFile, "message.identifier",msg_iden_seq);
  }

  if (validation_to!=null ){
    if (validation_to.length() > 24) {
      validation_to = Date.parse(format, validation_to.substring(0,23) + 'Z').getTime();
    } else {
      validation_to = Date.parse(format, validation_to).getTime();
	}
    session.putAttribute(flowFile, "validation.to",validation_to.toString());
  }

  // 3th processor
  def date = new Date();
  def msg_startTime = date.getTime();
  session.putAttribute(flowFile, "message.startTime",msg_startTime.toString());

  def msg_timeStampTime;
  if (msg_timeStamp!=null && ! msg_timeStamp.empty){
  	if (msg_timeStamp.length() > 29) {
      def msg_timeStampParse = Date.parse(format_timezone, msg_timeStamp);
      msg_timeStampTime = msg_timeStampParse.getTime();
    } else {
      def msg_timeStampParse= Date.parse(format, msg_timeStamp);
      msg_timeStampTime = msg_timeStampParse.getTime();
    }
    session.putAttribute(flowFile, "message.timestamp",msg_timeStampTime.toString());
  }
  //4th processor 
  if (validation_to==null  && validation_duration!=null ){
    validation_to = msg_timeStampTime + ((validation_duration.toInteger()) * duration_multiplier);
    session.putAttribute(flowFile, "validation.to",validation_to.toString());
  }
    //message.type
  session.putAttribute(flowFile, "message.type",amqp_exchange);
  //message.format
  def msg_formart='';
  if (amq_msg.equalsIgnoreCase('0') ){
    msg_formart='binary';
  }else if (amq_msg.equalsIgnoreCase('1')){
      msg_formart='base64';
  }else if(amq_msg.equalsIgnoreCase('2')){
    msg_formart='json';
  }else if (amq_msg.equalsIgnoreCase('3') || amq_msg.equalsIgnoreCase('4') || amq_msg.equalsIgnoreCase('5')){
    msg_formart='xml';
  }
  session.putAttribute(flowFile, "message.format",msg_formart);
  //message.key
  def msg_identifier_transform='';
  if (msg_iden_seq!=null){
    msg_identifier_transform =  msg_iden_seq;
  }else{
    msg_identifier_transform = msg_identifier;
  }
  def message_key = "GENERATOR" + "_"+amqp_exchange +"_"+ msg_identifier_transform;
  session.putAttribute(flowFile, "message.key",message_key);

  // 5TH process
  if (amqp_exchange.equalsIgnoreCase('denm')){
    // 6th process
    if (content.toString().contains("termination")){
      actionObject = "CANCEL";
      session.putAttribute(flowFile, "action","CANCEL");
    }else{
      actionObject = "UPDATE";
      session.putAttribute(flowFile, "action","UPDATE");
    }
  }

  // create JSONAttributes
  def json = JsonOutput.toJson(["message.identifier":msg_identifier_transform,"message.type":amqp_exchange,"message.format":msg_formart,"message.timestamp":msg_timeStampTime.toString(),"message.contentIndex":amq_msg,"customer.id":object.customerId,"provider":"GENERATOR","reference.longitude":reference_longitude,"reference.latitude":reference_latitude,"action":actionObject,"validation.to":validation_to,"message.startTime":msg_startTime.toString(),"message.key":message_key,"reference.time":ref_time]);
  session.putAttribute(flowFile, "metadata",json);

  session.transfer(flowFile, REL_SUCCESS);
  }else{
    session.transfer(flowFile, REL_FAILURE);
  }
}

