  def referenceTimestamp = '2004/01/01 00:00:00.000Z';

  if (flowFile!= null){
      def date_reference;
      def date_reference_timestamp;
      def newCurrentTimestamp;
      def dateHex;
      flowFile = session.write(flowFile, {inputStream, outputStream ->        
         try{ 
              def currentLatitude = Long.valueOf(flowFile.getAttribute("latitude"));
              def currentLongitude = Long.valueOf(flowFile.getAttribute("longitude"));
              def currentTimestamp = Long.valueOf(flowFile.getAttribute("messageTimestamp"));
              def inputStreamRed = IOUtils.toString(inputStream, StandardCharsets.UTF_8);
              def newLatitude;
              def newLongitude;
              if (currentLatitude<0){
                  newLatitude = currentLatitude + Long.valueOf(latlongCorrection); 
              }else {
                  newLatitude = currentLatitude - Long.valueOf(latlongCorrection); 
              }
              if (currentLongitude<0){
                  newLongitude = currentLongitude + Long.valueOf(latlongCorrection); 
              }else {
                  newLongitude = currentLongitude - Long.valueOf(latlongCorrection); 
              }
              def conversionLatitude = inputStreamRed.replaceAll('##Latitude##',newLatitude.toString());
              def conversionLongitude = conversionLatitude.replaceAll('##Longitude##',newLongitude.toString());
              date_reference = Date.parse(pattern, referenceTimestamp);
              date_reference_timestamp = date_reference.getTime();
              newCurrentTimestamp = currentTimestamp - date_reference_timestamp;
              dateHex = Long.toHexString(newCurrentTimestamp);

              def dateHexlength= dateHex.length();
              def hexDateTst = dateHex.substring((dateHexlength-8), dateHexlength);
			  def dateTst = Long.parseLong(hexDateTst, 16);
              def conversionTimestamp = conversionLongitude.replaceAll('##Timestamp##',dateTst.toString()); 

              outputStream.write(conversionTimestamp.getBytes(StandardCharsets.UTF_8));
         }catch(e) {
              log.error("Error during processing of validate.groovy", e)
              //session.transfer(flowFile, REL_FAILURE)
              fail=true
          }

      } as StreamCallback)

      if(fail){
          session.transfer(flowFile, REL_FAILURE)
          fail = false
      } else {
          session.putAttribute(flowFile, 'resultHex', Long.toHexString(newCurrentTimestamp));
          session.transfer(flowFile, REL_SUCCESS)
      }
  }
